#include <iostream>

using namespace std;

int main() {

    int x=5;
    int y=3;

    //1
    cout<<"1. x++ = "<<x++<<" nilai x sekarang = "<<x<<endl;

    //2
    cout<<"2. y++ = "<<y++<<" nilai x sekarang = "<<y<<endl;

    //3
    cout<<"3. x-- = "<<x--<<" nilai x sekarang = "<<x<<endl;

    //4
    cout<<"4. y-- = "<<y--<<" nilai x sekarang = "<<y<<endl;

    return 0;
}